changeSet.sql:
CREATE TRIGGER enforce_batch_constraints
    BEFORE INSERT ON tbl_batch
    FOR EACH ROW
BEGIN
    DECLARE user_count INT;
    DECLARE location_count INT;

    -- Check if the user is a trainer
    SELECT COUNT(*) INTO user_count
    FROM tbl_user
    WHERE id = NEW.user_id AND role = 'TRAINEE';

    IF user_count = 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'User must have the role of TRAINEE.';
    END IF;

    -- Check if the location is a CITY
    SELECT COUNT(*) INTO location_count
    FROM tbl_location
    WHERE id = NEW.location_id AND location_type = 'CITY';

    IF location_count = 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Location must have the type of CITY.';
    END IF;
END;
//

CREATE TRIGGER enforce_batch_constraints_update
    BEFORE UPDATE ON tbl_batch
    FOR EACH ROW
BEGIN
    DECLARE user_count INT;
    DECLARE location_count INT;

    -- Check if the user is a trainer
    SELECT COUNT(*) INTO user_count
    FROM tbl_user
    WHERE id = NEW.user_id AND role = 'TRAINEE';

    IF user_count = 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'User must have the role of TRAINER.';
    END IF;

    -- Check if the location is a CITY
    SELECT COUNT(*) INTO location_count
    FROM tbl_location
    WHERE id = NEW.location_id AND location_type = 'CITY';

    IF location_count = 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Location must have the type of CITY.';
    END IF;
END;
//
